SELECT ord.order_id ORDER_ID, ord.ORDER_NO ORDER_NO, device.deviceitem_uid DEVICEITEM_UID, ord.convergeBox_No CONVERGEBOX_NO, ifnull(mord.MORDER_ID, 0) MORDER_ID, mord.MORDER_NO MORDER_NO, mord.MAINTAIN_STATUS MAINTAIN_STATUS FROM (SELECT order_id, deviceitem_uid FROM qr_deviceitem item WHERE item.deviceitem_status=0 AND deviceitem_uid IN
#list[].convergeBoxNo#
) device LEFT JOIN qr_order ord ON device.order_id = ord.order_id and ord.order_status=0 LEFT JOIN qr_morder mord ON ord.ORDER_ID = mord.ORDER_ID and mord.MORDER_STATUS=0 ORDER BY ord.order_id desc ,mord.MORDER_ID desc
